queries.query:
query workspace_list {
  __typename
  workspacesList {
    count
    items {
      name
      id
    }
  }
}

query userInvitationsList {
  __typename
  userInvitationsList {
    count
    items {
      firstName
      email
      invitedBy {
        firstName
        workspaceName
      }
      uuid
    }
  }
}

query AppList {
  __typename
  applicationsList {
    count
    items {
      name
      id
      status
      createdAt
    }
  }
}

query System_info {
  __typename
  system {
    applicationsList {
      items {
        name
        description
        createdAt
        id
        status
        displayName
        appType
      }
      count
    }
  }
}

query teamInvitationsDetailsList {
  __typename
  teamInvitationsDetailsList {
    count
    items {
      firstName
      invitedBy {
        firstName
      }
      uuid
    }
  }
}

query FunctionList {
  __typename
  functionsList {
    count
    items {
      application {
        name
        id
      }
      description
      name
      functionType
      ... on FunctionWebhookInfo {
        description
        name
      }
      ... on FunctionTriggerInfo {
        description
        name
      }
      ... on FunctionResolverInfo {
        description
        name
      }
      ... on FunctionTaskInfo {
        description
        scheduleExpression
        name
      }
    }
  }
}

query userbillingConfig {
  __typename
  userBillingConfiguration {
    availablePlans {
      name
      price
      id
    }
    isCancelSubscriptionAvailable
  }
}

query tablelist {
  __typename
  tablesList {
    count
    items {
      displayName
      id
      dataFeatures {
        create
        delete
        update
      }
      description
      origin {
        provider
      }
      schemaFeatures {
        computedFields
      }
      name
    }
  }
}

query FileList {
  __typename
  filesList {
    count
    items {
      id
      filename
      shareUrl
      createdBy {
        id
        firstName
      }
    }
  }
}

query FileUploadInfo {
  __typename
  fileUploadInfo {
    path
    signature
    policy
    apiKey
  }
}

query teamInvitationsDetails {
  __typename
  teamInvitationDetails(uuid: "") {
    invitedBy {
      firstName
      workspaceName
    }
    firstName
    uuid
  }
}

query Description {
  __typename
  user(id: "ck9igaiwt000m08l0ee7pej3d") {
    _description
    email
    firstName
    id
    lastName
  }
}
